import React from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import ImagePopup from "./ImagePopup.js";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import api from "../utils/Api.js";
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import ProtectedRoute from "./ProtectedRoute";
import Login from "./Login.js";
import Register from "./Register.js";
import InfoTooltip from "./InfoTooltip.js";
import authApi from "../utils/AuthApi.js";

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  //const [isDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false);
  const [isSuccessPopupOpen, setIsSuccessPopupOpen] = React.useState(false);
  const [cards, setCards] = React.useState([]);

  // 12 спринт
  // статус входа
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  // попап успешного входа
  const [isInfoTolltipSuccess, setIsInfoTolltipSuccess] = React.useState(false);
  // хук история
  const navigate = useNavigate();
  // email
  const [hederEmail, setHeaderEmail] = React.useState("");
  // текст кнопки
  const [isLoading, setIsLoading] = React.useState(false); 

  //Получаем массив карточек и информацию о пользователе
  React.useEffect(() => {
    api
      .getCurrentUser()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log(err);
      });
    api  
      .getInitialCards()
      .then((cards) => {
        setCards(cards);
       })
      .catch((err) => {
         console.log(err);
     });
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((cards) => 
          cards.map((c) => c._id === card._id ? newCard : c));
        })
        .catch((err) => {
          console.log(err);
        });
  }
  function handleCardDelete(card) {
    api.deleteCard(card._id).then(() => {
      setCards((cards) => cards.filter((c) => c._id !== card._id));
    })
      .catch((err) => {
        console.log(err);
      });
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
    setIsSuccessPopupOpen(false);
  }

  function handleUpdateUser(item) {
    api
      .setUserInfo(item)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
      .finally(() => setIsLoading(false));
        setIsLoading(true);
  }

  function handleUpdateAvatar(data) {
    api
      .changeUserAvatar(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
      .finally(() => setIsLoading(false));
        setIsLoading(true);
  }

  function handleAddPlaceSubmit(item) {
    api
      .createCard(item)
      .then((res) => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err))
      .finally(() => setIsLoading(false));
        setIsLoading(true);
  }
// // закрытие по esc
// const isOpen =
// isEditAvatarPopupOpen ||
// isEditProfilePopupOpen ||
// isAddPlacePopupOpen ||
// isSuccessPopupOpen ||
// selectedCard;

// useEffect(() => {
// function closeByEscape(evt) {
//   if (evt.key === "Escape") {
//     closeAllPopups();
//   }
// }
// if (isOpen) {
//   // навешиваем только при открытии
//   document.addEventListener("keydown", closeByEscape);
//   return () => {
//     document.removeEventListener("keydown", closeByEscape);
//   };
// }
// }, [isOpen]);
   // 12 спринт
  // регистрация пользователя
  function handleRegisterUser(email, password) {
    authApi
      .registerUser(email, password)
      .then((data) => {
        if (data) {
          setIsInfoTolltipSuccess(true); // успешный вход
          navigate("/sing-in"); // переюрасываем на вход
        }
      })
      .catch((err) => {
        setIsInfoTolltipSuccess(false); // fail
        console.log(err);
      })
      .finally(() => setIsSuccessPopupOpen(true)); // в любом случае открываем попап
  }

  // аутентификация пользователя
  function handleAuthUser(email, password) {
    authApi
      .loginUser(email, password)
      .then((data) => {
        if (data.token) {
          setHeaderEmail(email); // передаем почту
          setIsLoggedIn(true); // войдено
          localStorage.setItem("jwt", data.token);
          navigate("/"); // перебрасываем в профиль
        }
      })
      .catch((err) => {
        setIsInfoTolltipSuccess(false); // fail
        setIsSuccessPopupOpen(true); // в любом случае открываем попап
        console.log(err);
      });
  }
  // проверяем токен
  React.useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      authApi
        .checkToken(jwt)
        .then((data) => {
          if (data) {
            setIsLoggedIn(true); // войдено
            setHeaderEmail(data.data.email); // получаем почту
            navigate("/"); // перебрасываем в профиль
          }
        })
        .catch((err) => console.log(err));
    }
  }, [navigate]);
  // удаляем токен
  function handleSingOut() {
    localStorage.removeItem("jwt");
    setHeaderEmail(""); // очищаем почту
    setIsLoggedIn(false); // не войдено
    navigate("/sign-in"); // перебрасываем на вход
  }
  return (
  <CurrentUserContext.Provider value={currentUser}>
    <div className="body">
      <>
      <Header onSignOut={handleSingOut} headerEmail={hederEmail} />
      </>
      <Routes>
      <Route
        exact
        path="/"
        component={Main}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
        cards={cards}
        onCardLike={handleCardLike}
        onCardDelete={handleCardDelete}
        isLoggedIn={isLoggedIn}
        element={<ProtectedRoute />}
      />
      <Route path="/sign-up">
        <Route onRegister={handleRegisterUser} element={<Register />}/>
      </Route>
      <Route path="/sign-in">
        <Route onLogin={handleAuthUser} element={<Login />}/>
      </Route>
      <Route>
        {isLoggedIn ? <Route to="/" element={<Navigate />}/> : <Route to="/sign-in" element={<Navigate />}/>}
      </Route>
      </Routes>
      <Footer />
      <EditProfilePopup 
        isOpen={isEditProfilePopupOpen} 
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateUser} 
        isLoading={isLoading}/>
      <AddPlacePopup
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        onAddPlace={handleAddPlaceSubmit}
        isLoading={isLoading} />
      <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
        isLoading={isLoading} />
      <PopupWithForm
        //isOpen={isDeletePopupOpen}
        name={"delete"}
        title={"Вы уверены?"}
        buttonText={"Да"}
        onClose={closeAllPopups}
      ></PopupWithForm>
      <ImagePopup
        card={selectedCard}
        onClose={closeAllPopups}
        //isOpen={selectedCard}
      />
      <InfoTooltip
        name={"success"}
        onClose={closeAllPopups}
        isOpen={isSuccessPopupOpen}
        isSuccess={isInfoTolltipSuccess}
          />
    </div>
  </CurrentUserContext.Provider>
  );
}

export default App;
