  //const [isInfoTolltipSuccess, setIsInfoTolltipSuccess] = React.useState(false);

  // email
  //const [hederEmail, setHeaderEmail] = React.useState("");
  // текст кнопки
  //const [isLoading, setIsLoading] = React.useState(false); 

  //Получаем массив карточек и информацию о пользователе
  useEffect(() => {
    api
      .getCurrentUser()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log(err);
      });
    api  
      .getInitialCards()
      .then((cards) => {
        setCards(cards);
       })
      .catch((err) => {
         console.log(err);
     });
  }, []);

  // проверяем токен пользователя, если у пользователя есть токен в localStorage, проверим, действующий он или нет
  useEffect(() => {
    if (localStorage.getItem("token")) {
      const token = localStorage.getItem("token");

      auth
        .checkToken(token)
        .then((res) => {
          if (res) {
            setLoggedIn(true);
            navigate("/", { replace: true });
            setUserEmail(res.data.email);
          }
        })
        .catch((err) => console.log(err));
    }
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((cards) => 
          cards.map((c) => c._id === card._id ? newCard : c));
        })
        .catch((err) => {
          console.log(err);
        });
  }
  function handleCardDelete(card) {
    api.deleteCard(card._id).then(() => {
      setCards((cards) => cards.filter((c) => c._id !== card._id));
    })
      .catch((err) => {
        console.log(err);
      });
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
    //setIsSuccessPopupOpen(false);
    setIsInfoTooltip(false);
  }

  function handleUpdateUser(item) {
    api
      .setUserInfo(item)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
      // .finally(() => setIsLoading(false));
      //   setIsLoading(true);
  }

  function handleUpdateAvatar(data) {
    api
      .changeUserAvatar(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
      // .finally(() => setIsLoading(false));
      //   setIsLoading(true);
  }

  function handleAddPlaceSubmit(item) {
    api
      .createCard(item)
      .then((res) => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
      //.finally(() => setIsLoading(false));
        //setIsLoading(true);
  }

   // обработчик регистрации пользователя
   const handleRegistrationSubmit = ({ email, password }) => {
    auth
      .register(password, email)
      .then(() => {
        setIsSuccess(true);
        setIsInfoTooltip(true);
        navigate("/sign-in", { replace: true });
      })
      .catch((err) => {
        console.log(err);
        setIsSuccess(false);
        setIsInfoTooltip(true);
      });
  };

  // обработчик авторизации пользователя
